<?php
session_start();


if (!isset($_SESSION['logged_in'])) {
    header('location: adminLogin.php');
    exit;
}
include ('./layout/sidebar.php');
include '../server/connection.php';

// Query total users
$userQuery = "SELECT COUNT(*) as total_users FROM users";
$userResult = mysqli_query($conn, $userQuery);
$userData = mysqli_fetch_assoc($userResult);
$totalUsers = $userData['total_users'];

// Query total reservations
$reservationQuery = "SELECT COUNT(*) as total_reservations FROM reservasi";
$reservationResult = mysqli_query($conn, $reservationQuery);
$reservationData = mysqli_fetch_assoc($reservationResult);
$totalReservations = $reservationData['total_reservations'];

// Query total revenue
$revenueQuery = "SELECT SUM(amount) as total_revenue FROM payments WHERE payment_status = 'confirmed' OR payment_status = 'expired'";
$revenueResult = mysqli_query($conn, $revenueQuery);
$revenueData = mysqli_fetch_assoc($revenueResult);
$totalRevenue = $revenueData['total_revenue'] ?? 0;

// Query for monthly orders
$monthlyOrderQuery = "SELECT MONTH(reservation_date) as month, COUNT(*) as orders 
                      FROM reservasi 
                      GROUP BY month 
                      ORDER BY month";
$monthlyOrderResult = mysqli_query($conn, $monthlyOrderQuery);
$monthlyOrders = array_fill(1, 12, 0); // Inisialisasi 12 bulan
while ($row = mysqli_fetch_assoc($monthlyOrderResult)) {
    $monthlyOrders[(int)$row['month']] = (int)$row['orders'];
}
$monthlyOrderData = json_encode(array_values($monthlyOrders));

// Query average rating
$reviewAvgQuery = "SELECT AVG(rating) as avg_rating FROM review";
$reviewAvgResult = mysqli_query($conn, $reviewAvgQuery);
$reviewData = mysqli_fetch_assoc($reviewAvgResult);
$averageRating = round($reviewData['avg_rating'], 1);
?>

